import * as actionTypes from '../actions';
import * as cardAttributes from '../../components/Game/Cards/cardAttributes';

const initialState = {
    results: [false,false,false,false,false,false,false,false,false,false],
    allCards:{
        cards:[{position:19},
            {position:19},
            {position:19},
            {position:19},
            {position:19},
            {position:19},
            {position:19},
            {position:19},
            {position:19},
            {position:19},
            {position:19},
            {position:19}],
        cardOpponent:[{position:19},
            {position:19},
            {position:19},
            {position:19},
            {position:19},
            {position:19},
            {position:19},
            {position:19},
            {position:19},
            {position:19},
            {position:19},
            {position:19}]
    }
};

const reducer = ( state = initialState, action) => {

    switch ( action.type ) {
        case 'PLAY_THE_CARD':
            const newResults={...state.results};
            newResults[action.id]=action.val;
                return {...state,
                    results:{...newResults},
                }
        case 'GIVEAWAYTHECARS':
            const newState={...this.allCards};
            newState.cards.map((card)=>{
                card.position=30;
            });
            newState.cardOpponent.map((card)=>{
                card.position=30;
            });
            this.setState({...state,
                allCards:{...newState}});
        default:
            return state;
    }
};

export default reducer;
