import React, { Component } from 'react';
import Cards from '../../components/Game/Cards/Cards';
import styles from './Game.module.css';
class Builder extends Component{
    state={
        cards:[{position:false}]
    }
    render(){
        const card=this.state.cards.map((counter)=>{
            let styleCard=[styles.DeckCard];
            if(counter.position){
                styleCard=[styles.DeckCard];
                alert();
            }

            return <Cards key={counter} styleCard={styleCard.join(' ')}/>
        })
        return(
            <div className={styles.GameDiv}>
            <button>Start Game</button>
                {card}
            </div>
        );
    }
}

export default Builder;
