import React, { Component } from 'react';
import Cards from '../../components/Game/Cards/Cards';
import styles from './Game.module.css';
class Builder extends Component{
    state={
        cards:[{position:false},
            {position:false},
            {position:false},
            {position:false},
            {position:false},
            {position:false}]
    }
    action=()=>{
        this.setState({
            cards:[{position:true},{position:true},{position:true},{position:true},{position:true},{position:true},]
        });
    }
    render(){
        const card=this.state.cards.map((counter,i)=>{
            let styleCard=null;
            if(counter.position){
                const lenght=this.state.cards.length;
                styleCard={transform:"translate3d(-"+(-i*(300/lenght)+650)+"%,-30%,"+(i+5)+"px) rotate("+-30+(60/lenght)*i+"deg)",};
            }

            return <Cards key={i} stylesCard={styleCard}/>
        })
        return(
            <div className={styles.GameDiv}>
            <button onClick={this.action}>Start Game</button>
                {card}
            </div>
        );
    }
}

export default Builder;
