import React, { Component } from 'react';
import Cards from '../../components/Game/Cards/Cards';
import styles from './Game.module.css';
class Builder extends Component{
    state={
        cards:[{position:false},
            {position:false},
            {position:false},
            {position:false},
            {position:false},
            {position:false}]
    }
    action=()=>{
        this.setState({
            cards:[{position:true}]
        });
    }
    render(){
        const card=this.state.cards.map((counter,i)=>{
            let styleCard=null;
            if(props.position){
                styleCard={};
                alert();
            }
            return <Cards key={i} stylesCard={stylesCard} position={counter.position}/>
        })
        return(
            <div className={styles.GameDiv}>
            <button onClick={this.action}>Start Game</button>
                {card}
            </div>
        );
    }
}

export default Builder;
